import React, { useState } from 'react';



const Form = () => {

    const [name, setName] = useState("");
    const [fullName,setFullName] = useState("");

    const inputEvent = (e) => {
        let value = e.target.value;

        setName(value);

    }
    const submit = (e) => {
        console.log(e.target.value);
        setFullName(name);
    }
    return (
        <>

            <h1 className="form">HELLO {fullName}</h1>
            <input type="text" placeholder="Enter your name" className="buttonD" onChange={inputEvent}></input>
            <input type="text" placeholder="Enter your name" className="buttonF" onChange={inputEvent}></input>
            <button className="buttonE" onClick={submit}>SUBMIT</button>
        </>
    );

};

export default Form;
