import React, { useState } from 'react';



const Form = () => {

    const [name, setName] = useState("");
    const [lName,setLName] = useState("");

    const [fullName, setFullName] = useState("");

    const inputEvent = (e) => {

        let value = e.target.value;

        setName(value);

    }
    const inputEventTwo = (e)=>{
        let valueTwo = e.target.value;

        setLName(valueTwo);

    }
    const submit = (e) => {
        e.preventDefault();
        console.log(e.target.value);
        setFullName(name+" "+lName);
    }
    return (
        <>
            <div>
                <form onSubmit={submit}>
                    <h1 className="form">HELLO {fullName}</h1>
                    <input type="text" placeholder="Enter your first name" className="buttonD" onChange={inputEvent} value={name}></input>
                    <input type="text" placeholder="Enter your last name" className="buttonF" onChange={inputEventTwo} value={lName}></input>
                    <button type="submit" className="buttonE">SUBMIT</button>
                </form>
            </div>
        </>
    );

};

export default Form;
