import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css";


const style = {
  color: "black",
  textAlign: "center"
};
const heading = {
  color: "yellow"
};
let currDate = new Date();
currDate = currDate.getHours();
let greeting = "";
if (currDate >= 1 && currDate < 12) {
  greeting = "Good Morning";
  style.color = "green";
}
else if (currDate >= 12 && currDate < 19) {
  greeting = "Good Afternoon";
  style.color = "orange";
}
else {
  greeting = "Good Night";
  style.color = "blue";
}
ReactDOM.render(
  <>
    <div>
      <h1 style={heading}>
        Hello sir/Madam, <span style={style}>{greeting}</span>
      </h1>

    </div>
  </>, document.getElementById('root')
);
