import React, { useState } from 'react';



const App = () => {
    const initBg = "antiquewhite";
    const [Bg, setBg] = useState(initBg);

    const [count, setCount] = useState(0);

    let newTime = new Date().toLocaleTimeString();
    const [time, setTime] = useState(newTime);

    let newcTime = new Date().toLocaleTimeString();
    const [ctime, setcTime] = useState(newcTime);


    const IncNum = () => {
        setCount(count + 1);

        setBg("cornflowerblue");

    }

    const IncTime = () => {
        let newTime = new Date().toLocaleTimeString();

        setTime(newTime);

    }

    const timeNow = () => {
        let newcTime = new Date().toLocaleTimeString();
        console.log(newcTime);

        setcTime(newcTime);

    }

    setInterval(timeNow, 1000);

    return (
        <>
            <div className="body" style={{ backgroundColor: Bg }}>"
                <h3 onLoad={timeNow}><span>TIME :</span>{ctime}</h3>

                <h1><span>CLICK ME TO INCREASE NUMBERS:</span>{count}</h1>
                <button className="buttonA" onClick={IncNum}>Click Me</button>

                <h2><span>CLICK ME TO
                GET CURRENT TIME:</span>{time}</h2>
                <button className="buttonB" onClick={IncTime}> Get Time</button>

            </div>

        </>
    );

};

export default App;
